import React,{useState} from "react";
import Introduction from "./Introduction";
import Plan from "./Plan";
import icon from "../asset/icon2.jpeg";

function Page() {
    const[page, setPage] = useState(1);
    return (
      <div>
        <img src={icon}>
        </img>
        <span onClick={() => setPage(1)}>引言</span>

        <button onClick={() => setPage(2)}>計劃因為</button>

        {page === 1 && <Introduction/>}

        {page === 2 && <Plan/>}
        </div>
    );
  }
  export default Page;
