import React,{useState} from "react";
import Introduction from "./Introduction";
import Plan from "./Plan";
import icon from "../asset/icon2.jpeg";
import "./Page.css";

function Page() {
    const[page, setPage] = useState(1);
    return (
      <div>
        <img src={icon}>
        </img>
        <div className="btnContainer">

      <div className={page === 1 ? "clicked" :"btn"} onClick={() => setPage(1)}>1</div>

      <div className={page === 2 ? "clicked" :"btn"} onClick={() => setPage(2)}>2</div>
      <div className={page === 3 ? "clicked" :"btn"} onClick={() => setPage(3)}>3</div>
      <div className={page === 4 ? "clicked" :"btn"} onClick={() => setPage(4)}>4</div>

      </div>
      {page === 1 && <Introduction/>}

      {page === 2 && <Plan/>}

      {page === 3 && <Introduction/>}

      {page === 4 && <Plan/>}
      </div>
    );
  }
  export default Page;
