import React,{useState}from "react";
import Page from "./Page.js";
import "./Content.css";
import Plan from "./Plan.js";


function Content() {
  const[page, setPage] = useState(1);
  const[subTitle, setsubTitle] = useState("莘莘入場：學校文化日計劃");
  function title(number){setPage(number)
  if (number == 1){setsubTitle("莘莘入場：學校文化日計劃")}}
  return (
    <div>

    <a>主頁></a><b>{subTitle}</b>
    <div className="btnContainer">

      <div className={page === 1 ? "clicked" :"btn"} onClick={() => title(1)}>莘莘入場：學校文化日計劃</div>

      <div className={page === 2 ? "clicked" :"btn"} onClick={() => setPage(2)}>少年登台：學校演藝實踐計劃</div>
      <div className={page === 3 ? "clicked" :"btn"} onClick={() => setPage(3)}>青年探藝：高中生藝術新體驗計劃</div>
      <div className={page === 4 ? "clicked" :"btn"} onClick={() => setPage(4)}>天空故事：初小校園藝術計劃</div>

      </div>
      {page === 1 && <Page/>}

      {page === 2 && <Plan/>}

      {page === 3 && <Page/>}

      {page === 4 && <Plan/>}
      </div>
  );
}
export default Content;
