import React, { useState } from "react";
import Introduction from "./Introduction";
import Plan from "./Plan";

import "./Page.css";

function Page() {
  const [page, setPage] = useState(1);
  return (
    <div>
      <div className="btnContainer">
        <div
          className={page === 1 ? "clicked" : "btn"}
          onClick={() => setPage(1)}
        >
          引言
        </div>

        <div
          className={page === 2 ? "clicked" : "btn"}
          onClick={() => setPage(2)}
        >
          引言引言
        </div>
        <div
          className={page === 3 ? "clicked" : "btn"}
          onClick={() => setPage(3)}
        >
          引言引言
        </div>
        <div
          className={page === 4 ? "clicked" : "btn"}
          onClick={() => setPage(4)}
        >
          引言引言
        </div>
        <div
          className={page === 5 ? "clicked" : "btn"}
          onClick={() => setPage(5)}
        >
          引言引言
        </div>

        <div
          className={page === 6 ? "clicked" : "btn"}
          onClick={() => setPage(6)}
        >
          引言引言
        </div>
        <div
          className={page === 7 ? "clicked" : "btn"}
          onClick={() => setPage(7)}
        >
          引言引言
        </div>
        <div
          className={page === 8 ? "clicked" : "btn"}
          onClick={() => setPage(8)}
        >
          引言引言
        </div>
      </div>
      {page === 1 && <Introduction />}

      {page === 2 && <Plan />}

      {page === 3 && <Page />}

      {page === 4 && <Plan />}

      {page === 5 && <Introduction />}

{page === 6 && <Plan />}

{page === 7 && <Page />}

{page === 8 && <Plan />}
    </div>
  );
}
export default Page;
